sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   if(newValue !== ""){
   g_form.setSectionDisplay("device_details",true);
   g_form.setSectionDisplay('delivery_details',true);

   g_form.getReference('u_reference_1',getDetails); //get reference pulls all the information from the reference field. Its better to use Glide Ajax.
   }

	function getDetails(details){

		g_form.setValue('u_device_model', details.model_number);
		g_form.setValue('u_price', details.u_price_2);
		g_form.setValue('u_device_description', details.short_description);
		g_form.setValue('u_warranty', details.warranty_expiration);

		}




   //Type appropriate comment here, and begin script below

}
